import React, { Component } from "react";
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Request from "../helpers/requests.js";
import "./css/RestaurantViewContainer.css";

import BookingList from "../Components/BookingListComponent.js";
import TableList from "../Components/TableListComponent.js";

import NavBar from "../NavBar.js";

class RestaurantViewContainer extends Component {
  constructor(props) {
    super(props)
    this.state = {
      tables: [],
      customers: [],
      bookings: [],
      formValues: {
        date: "",
        time: ""}
    }

    this.handleDateChange = this.handleDateChange.bind(this);
    this.handleTimeChange = this.handleTimeChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  componentWillMount() {
    const request = new Request();
    request.get('restaurantTables/')
      .then(res => {
        this.setState({tables: res._embedded.restaurantTables})
      })

    request.get('customers/')
      .then(res => {
        this.setState({customers: res._embedded.customers})
      })

  }

  getBookings(url) {
    const request = new Request();
    request.get(url)
      .then(res => {
        this.setState({bookings: res._embedded.bookings})
      })
  }

  handleDateChange(event) {
    this.setState({formValues: { date: event.target.value} })
  }

  handleTimeChange(event) {
    this.setState({formValues: { time: event.target.value} })
  }

  handleSubmit(event) {
    event.preventDefault()
    const date = this.state.formValues.date;
    const time = this.state.formValues.time;
    this.getBookings(`bookings/date/${date}/time/${time}`)
  }

  render() {
    return(
      <React.Fragment>
        <Router>
          <NavBar/>
          <div className="top-section">
            <form onSubmit={this.handleSubmit}>
              Date <input type="text" onChange={this.handleDateChange} />
              Time <input type="text" onChange={this.handleTimeChange} />
              Search <input type="submit" />
            </form>
          </div>
          <div className="grid">
            <TableList tables={this.state.tables}/>
            <BookingList bookings={this.state.bookings}/>
          </div>
        </Router>
      </React.Fragment>

    )
  }
}

export default RestaurantViewContainer;
