import React, {Component } from "react";
import Request from "../helpers/requests.js"

class EditBookingComponent extends Component {
  constructor(props) {
    super(props)
    this.state = {
      currentBooking: null,
      id: this.props.id,
      date: null,
      time: null,
      table: null
    }
    this.handleUpdate = this.handleUpdate.bind(this)
    this.handleTableChange = this.handleTableChange.bind(this)
    this.handleDateChange = this.handleDateChange.bind(this)
    this.handleTimeChange = this.handleTimeChange.bind(this)

}

  // componentWillMount() {
  //   for (const booking of this.props.allBookings) {
  //     if (booking.id == this.state.id) {
  //       this.setState({date: booking.date,
  //         time: booking.time,
  //         table: booking.restaurantTable.tableNumber})
  //     }
  //   }
  // }

  handleDateChange(event) {
    this.setState({date: event.target.value})
  }

  handleTimeChange(event) {
    this.setState({time: event.target.value})
  }

  handleTableChange(event) {
    this.setState({table: event.target.value})
  }

  handleUpdate(event) {
    event.preventDefault()

    this.verifyDuplicate()
    // request.patch('bookings/', {})
}
  verifyDuplicate() {
    // for (const existingBooking of this.props.allBookings) {
    //   if (existingBooking.
    // }
  }



  render() {
    const tables = this.props.tables.map((table, index) => {
      return <option key={index} value={table.tableNumber}>{table.tableNumber}</option>
    })
    return (<div>
      <form onSubmit={this.handleUpdate}>
        Table number: <input type="text" onChange={this.handleTableChange} />
        New Date <input type="text" onChange={this.handleDateChange} />
        New Time <input type="text" onChange={this.handleTimeChange} />
        Save <input type="submit" />
      </form>
    </div>)
  }
}


export default EditBookingComponent;
